\begin{center}
		\begin{tabular}{|c|c|c|c|c|c|c|c|}
		\hline \textbf{Nombre d'entretiens :} & 0 & 1 & 2 & 3 & 4 & 5 & 6 et plus \\ 
		\hline \textbf{Effectifs :} & 15 & 30 & 27 & 13 & 9 & 5 & 1 \\ 
		\hline 
	\end{tabular} 
\end{center}
